import { Link, Navigate, Route, Routes } from "react-router-dom";
import { AuthData } from "../../context/AuthWrapper";
import { nav } from "./navigation";


export const RenderRoutes = () => {

        const { user } = AuthData();
        
        return (
             <Routes>
             { nav.map((r, i) => {
                  
                  if (r.isPrivate && user.isAuthenticated) {
                       return <Route key={i} path={r.path} element={r.element}/>
                  } else if (!r.isPrivate) {
                       return <Route key={i} path={r.path} element={r.element}/>
                  } else return false
             })}
             
             </Routes>
        )
   }
   
   export const RenderMenu = () => {
   
        const { user, logout } = AuthData()
   
        const MenuItem = ({r}) => {
             return (
                  <div className="menuItem"><Link to={r.path}>{r.name}</Link></div>
             )
        }
        return (
          <div className="menu">
          {nav.filter(r => r.isMenu && (!r.isPrivate || user.isAuthenticated))
              .map((r, i) => (
                <MenuItem key={i} r={r} />
              ))
          }
        
          {user.isAuthenticated ? (
            <div className="menuItem text-center">
              <span className="inline-block mr-10">Hello {user.name}</span>
              <Link to="/" onClick={logout}>Log out</Link>
            </div>
          ) : (
            <Navigate to="/" replace={true} />
          )}
        </div>
        
        )
   }
